import React from 'react';
import '../../App.css';
import { Login, Registration } from "../Login";

class SignUp extends React.Component {

constructor(props){
  super(props);
  this.state = {
    isLogginActive: true,

  }
} 

changeState() {
  const { isLogginActive } = this.state;
  //if(isLogginActive)
}
render() {
  const { isLogginActive } = this.state;
  const current = isLogginActive ? "Registration" : "Login";
  const currentActive = isLogginActive ? "login" : "Registration";
  return (
    <div className="SignUp">
      <div className="login">
        <div className="container">
        {isLogginActive && <Login containerRef={(ref) => this.current = ref}/>}
        {!isLogginActive && <Registration containerRef={(ref) => this.current = ref}/>}
        </div>
        <RightSide current={this.current} containerRef={ref => this.rightSide = ref} onClick={this.changeState.bind(this)}/>
      </div>
    </div>
  )
} 
}

const RightSide = props => {
  return (
<div className="right-side" ref={props.containerRef} onClick={props.onClick}>
 <div className="inner-container">
<div className="text">
{props.current}
</div>
 </div>
</div>
  );
}

export default SignUp;
